const express = require('express');
const mysql = require('mysql2');
const cors = require('cors');
require('dotenv').config();

const app = express();
app.use(cors());
app.use(express.json());

// ================= DB CONNECTION =================
const db = mysql.createConnection({
  host: process.env.DB_HOST || 'localhost',
  user: process.env.DB_USER || 'root',
  password: process.env.DB_PASSWORD || 'Adi@1804',
  database: process.env.DB_NAME || 'primesynthetics'
});

db.connect(err => {
  if (err) {
    console.error('DB connection error:', err);
  } else {
    console.log('âœ… Connected to MySQL database');
  }
});

// ================= REGISTER =================
app.post('/register', (req, res) => {
  const { name, email, password } = req.body;
  if (!name || !email || !password)
    return res.status(400).json({ message: 'All fields required' });

  db.query('SELECT * FROM users WHERE email = ?', [email], (err, results) => {
    if (err) return res.status(500).json(err);
    if (results.length > 0)
      return res.status(400).json({ message: 'User already exists, please login.' });

    db.query(
      'INSERT INTO users (name,email,password) VALUES (?,?,?)',
      [name, email, password],
      (err, results) => {
        if (err) return res.status(500).json(err);
        res.json({ message: 'User registered successfully', id: results.insertId });
      }
    );
  });
});

// ================= LOGIN =================
app.post('/login', (req, res) => {
  const { email, password } = req.body;
  if (!email || !password)
    return res.status(400).json({ message: 'All fields required' });

  db.query(
    'SELECT * FROM users WHERE email = ? AND password = ?',
    [email, password],
    (err, results) => {
      if (err) return res.status(500).json(err);
      if (results.length === 0)
        return res.status(400).json({ message: 'Invalid credentials' });

      const user = results[0];

      // login history entry
      db.query(
        'INSERT INTO login_history (user_id, email) VALUES (?, ?)',
        [user.id, user.email],
        (err2) => {
          if (err2) console.error('Login log insert error:', err2);
        }
      );

      res.json({ message: 'Login successful', user });
    }
  );
});

// ================= LOGIN HISTORY =================
app.get('/login-history', (req, res) => {
  db.query('SELECT * FROM login_history ORDER BY login_time DESC', (err, results) => {
    if (err) return res.status(500).json(err);
    res.json(results);
  });
});

// ================= START SERVER =================
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => console.log(`ğŸš€ Server running on port ${PORT}`));
